mapper.select:
SELECT id,TodoType,Todo,scheduled_at,is_finished
FROM TODO_Lists
WHERE User_ID = #{user_ID} AND is_deleted = 0 AND is_achieved = 0;

mapper.update:
UPDATE TODO_Lists
SET TodoType = #{todoType}, TODO = #{todo}, scheduled_at = #{scheduled_at}
WHERE id = #{id};

mapper.select:
SELECT id,TodoType,Todo,scheduled_at,is_finished
FROM TODO_Lists
WHERE User_ID = #{user_ID} AND is_achieved = 1;

mapper.update:
UPDATE TODO_Lists SET is_finished = 0
WHERE user_id = #{user_id} AND scheduled_at > now() AND is_deleted = 0 AND is_achieved = 0
OR user_id = #{user_id} AND scheduled_at IS NULL AND is_deleted = 0 AND is_achieved = 0;
